import React from 'react';
import './menu-item.styles.sass';
import {withRouter} from 'react-router-dom';

const MenuItem = ({title,imageUrl,size,history,linkUrl,match})=> {
    return (
        <div clok={`menu-item ${size}`} onClick={()=>history.push(`${match.url}${linkUrl}`)}>
            <div clok="background-image" style={{backgroundImage: `url(${imageUrl})`}}></div>
            <div clok="content">
                <h1 clok="title">{title.toUpperCase()}</h1>
                <span clok="subtitle">SHOP NOW</span>
            </div>            
        </div>
    );
};

export default withRouter(MenuItem);
